package examples;

public class DecimalPartNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double number=34256.8976;
		int decimal=(int)number;
		double fraction=number-decimal;
		System.out.println("The decimal part of the above number is "+decimal);
		System.out.println("The fractional part of the above number is "+fraction);
		

	}

}
